import React, { useState } from 'react';
import './LoginSignup.css';
import user_icon from '../Assets/person.png';
import email_icon from '../Assets/email.png';
import password_icon from '../Assets/password.png';
import exerly_logo from '../Assets/ExerlyLogo.jpg';
import { useNavigate } from 'react-router-dom';

const LoginSignup = () => {
  const [action, setAction] = useState("Sign Up");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async () => {
    const endpoint = action === "Sign Up" ? "/signup" : "/login";
    const payload = action === "Sign Up"
      ? { name, email, password }
      : { email, password };

    try {
      const response = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const data = await response.json();

      if (!response.ok) {
        alert(data.error || "Something went wrong.");
        return;
      }

      if (action === "Sign Up") {
        alert("Sign up successful! Now log in.");
        setAction("Login");
      } else {
        alert("Login successful!");
        navigate("/dashboard");
      }

    } catch (err) {
      alert("Failed to connect to server.");
      console.error(err);
    }
  };

  return (
    <div className="container">
      <div className="header">
        <img src={exerly_logo} alt="Exerly Logo" className="logo" />
        <h2>{action}</h2>
        <div className="underline"></div>
      </div>
      <div className="inputs">
        {action === "Sign Up" && (
          <div className="input">
            <img src={user_icon} alt="User" />
            <input type="text" placeholder="Name" onChange={(e) => setName(e.target.value)} />
          </div>
        )}
        <div className="input">
          <img src={email_icon} alt="Email" />
          <input type="email" placeholder="Email" onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div className="input">
          <img src={password_icon} alt="Password" />
          <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
        </div>
      </div>

      <div className="submit-container">
        <button
          className={action === "Sign Up" ? "submit gray" : "submit"}
          onClick={handleSubmit}
        >
          {action}
        </button>
        <button
          className={action === "Login" ? "submit gray" : "submit"}
          onClick={() => setAction(action === "Login" ? "Sign Up" : "Login")}
        >
          {action === "Login" ? "Sign Up" : "Login"}
        </button>
      </div>
    </div>
  );
};

export default LoginSignup;
